import React, { useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchCharacter,
  removeSelectedCharacter,
 
} from "../Redux/Actions/characterAction";


const CharacterDetails = () => {
  
  let character = useSelector((state) => state.character);
  const { characterId } = useParams();
  const { aliases,name, gender,born,culture} = character;
  const dispatch = useDispatch();
  // console.log(characterId)
  // console.log(character)

 
  useEffect(() => {
    if (characterId && characterId !== "") dispatch(fetchCharacter(characterId));
    return () => {
      dispatch(removeSelectedCharacter());
    };
  }, []);

  return (
    <div className="container" >
      {Object.keys(character).length === 0 ? (
        <h1 style={{margin:'30px'}}>...Loading</h1>
      ) : (
        <div className="card" style={{width: '38rem',width: '38rem'}}>
        <div className="card-body">
         <h1 className="card-title">{name}</h1>
         <h2 className="card-text">culture:{culture}</h2>
         <h2 className="card-text">aliases:{aliases}</h2>
         <h2 className="card-text">born:{born}</h2>
         <h2 className="card-text">gender:{gender}</h2>
         </div>
         </div>
      )}
    </div>
  );
};
export default CharacterDetails;
