import React from 'react'
import CharacterComponent from './CharacterComponent';
import { useEffect, useCallback, useMemo,useState } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { setCharacters } from "../Redux/Actions/characterAction";
import ReactPaginate from 'react-paginate';

const CharacterListing = ()=> {

  const [postsPerPage] = useState(5);
 const [offset, setOffset] = useState(1);
 const [posts, setAllPosts] = useState([]);
 const [pageCount, setPageCount] = useState(0)
  const characters = useSelector((state) => state);
  // console.log(characters)
  // // .allCaracters.characters);
  const dispatch=useDispatch();
  const fetchCharacters= async () => {
          const response = await axios
            .get(`https://www.anapioficeandfire.com/api/characters?page=${characters.currPage}&pageSize=500`)
            .catch((err) => {
              console.log("Err: ", err);
            });
            // console.log(response.data);
          dispatch(setCharacters(response.data));
        };
        
        useEffect(() => {
          fetchCharacters();
        },[]);

        console.log("Characters:" ,characters);

  return (
    <>
    <div className='ui grid container'>
       <CharacterComponent/>
    </div>
    <div className="ui pagination menu" >
     <ReactPaginate
                  previousLabel={"prev"}
                  nextLabel={"next"}
                  breakLabel={"..."}
                  breakClassName={"break-me"}
                  // pageCount={pageCount}
                  marginPagesDisplayed={2}
                  pageRangeDisplayed={5}
                  // onPageChange={handlePageClick}
                  containerClassName={"pagination"}
                  subContainerClassName={"pages pagination"}
                  activeClassName={"active"}/>
  </div>
  
    </>
    
  )

  }
export default CharacterListing
