import React from 'react';
import { useSelector } from "react-redux";
import {Link} from "react-router-dom"

const CharacterComponent = () => {
  const characters = useSelector((state) => state.allCharacters.characters);
  // console.log("Characters:" ,characters);
  const renderList=characters.map((character)=>{
    let id=1;
    const { name, gender,culture} = character;
    
    return(
      <div className="three wide column">
        {/* <Link to={}></Link> */}
        <div className="ui link cards" style={{marginTop:'5px'}}>
          <div className="card">
                 {id}
            <div className="content">
               <div className="header"> {name}</div>
               <div className='meta gender'>{gender}</div>
               <div className='culture'>{culture}</div>
            </div>
          </div>
        </div>
    </div>
    )
    
  });
  return  <>{renderList}</>

}


export default CharacterComponent
