import React from 'react'
import CharacterComponent from './CharacterComponent';
import { useEffect, useState,useCallback, useMemo } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { setCharacters ,fetchCharacters} from "../Redux/Actions/characterAction";



const CharacterListing = ()=> {
  const [page, setPageNum] = useState(1);
  // const { data: posts, isLoading, isFetching } = useListPostsQuery(page);
  

  const characters = useSelector((state) => state);
  const dispatch=useDispatch();
 
        useEffect(() => {
        dispatch ( fetchCharacters(page));
        },[dispatch]);

        console.log("Characters:" ,characters);

        // handlePage(activePage) {
        //   const url = `https://www.anapioficeandfire.com/api/characters?page=1&pageSize=10`
        //   dispatch(fetchProducts(url))
        // }

  return (
    <div className='ui grid container'>
       <CharacterComponent/>
      
    <ul class="pagination justify-content-center">
      <li class="page-item"><a class="page-link"onClick={()=>setPageNum((page)=>page-1)} >Previous</a></li>
      
      <li class="page-item"><a class="page-link" onClick={()=>setPageNum((page)=>page+1)}>Next</a></li>
    </ul>
  
    </div>
  
  )

  }
export default CharacterListing
