import { ActionTypes } from "../Constants/action-types";
const intialState = {
  characters: [],
};

export const characterReducer = (state = intialState, { type, payload }) => {
  switch (type) {
    case ActionTypes.SET_CHARACTERS:
      return { ...state, characters: payload };
    default:
      return state;
  }
};

