import React from 'react'
import CharacterComponent from './CharacterComponent';
import { useEffect, useState,useCallback, useMemo } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { setCharacters ,fetchCharacters} from "../Redux/Actions/characterAction";



const CharacterListing = ()=> {
  const [page, setPageNum] = useState(1);
  const [characterId, setCharacterId] = useState(0);
   

  const characters = useSelector((state) => state);
  const dispatch=useDispatch();
 
        useEffect(() => {
        dispatch ( fetchCharacters(page));
        },[page]);

        console.log("Characters:" ,characters);

  return (
    <div className='ui grid container'>
       <CharacterComponent characterId={characterId}/>
      
    <ul className="pagination justify-content-center">
      <li className="page-item"><a className="page-link"onClick={()=>{
                                                                     setPageNum((page)=>page-1);
                                                                     setCharacterId((characterId)=>characterId-50)
                                                                     }} >Previous</a></li>
      
      <li className="page-item"><a className="page-link" onClick={()=>{
                                                                     setPageNum((page)=>page+1);
                                                                     setCharacterId((characterId)=>characterId+50)
                                                                     }} >Next</a></li>
    </ul>
    <div class="card" style="width: 18rem;">
  <img src="..." class="card-img-top" alt="..."/>
  <div class="card-body">
    <h5 class="card-title">Card title</h5>
    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
  </div>
  <ul class="list-group list-group-flush">
    <li class="list-group-item">An item</li>
    <li class="list-group-item">A second item</li>
    <li class="list-group-item">A third item</li>
  </ul>
  <div class="card-body">
    <a href="#" class="card-link">Card link</a>
    <a href="#" class="card-link">Another link</a>
  </div>
</div>
    </div>
  
  )

  }
export default CharacterListing
