import React from 'react';
import { useSelector } from "react-redux";
import {Link} from "react-router-dom"

const CharacterComponent =({characterId}) => {
  
  const characters = useSelector((state) => state.allCharacters.characters);
  const renderList=characters.map((character)=>{
    characterId++;
    const {name, gender,culture,aliases,born} = character;
    
    
    return(
      <div className="cardholder" key={characterId}>
        <Link to={`characters/${characterId}`}>
        <div className="card" style={{'width': '18rem'}}>
  <div className="card-body">
    <h5 className="card-title">{name}</h5>
    <p className="card-text">{aliases}</p>
  </div>
  <ul className="list-group list-group-flush">
  <li className="list-group-item">{born}</li>
    <li className="list-group-item">{gender}</li>
    <li className="list-group-item">{culture}</li>
  </ul>
</div>
        </Link>
    </div>
    )
    
  });
  return  <>{renderList}</>

}


export default CharacterComponent
