import React from 'react';
import { useSelector } from "react-redux";
import {Link} from "react-router-dom"

const CharacterComponent =({characterId}) => {
  
  const characters = useSelector((state) => state.allCharacters.characters);
  const renderList=characters.map((character)=>{
    characterId++;
    const {name, gender,culture} = character;
    
    
    return(
      <div className="three wide column" key={characterId}>
        {/* <Link to={`https://www.anapioficeandfire.com/api/characters/${id}`}> */}
        <Link to={`characters/${characterId}`}>
        <div className="cards" style={{marginTop:'5px'}}>
          <div className="card">
                 {characterId}
            <div className="content">
               <div className="header"> {name}</div>
               <div className='meta gender'>{gender}</div>
               <div className='culture'>{culture}</div>
            </div>
          </div>
        </div>
        <div class="card" style="width: 18rem;">
  <div class="card-body">
    <h5 class="card-title">Name</h5>
    <p class="card-text">{aliases}</p>
  </div>
  <ul class="list-group list-group-flush">
    <li class="list-group-item">{name}</li>
    <li class="list-group-item">{gender}</li>
    <li class="list-group-item">{culture}</li>
  </ul>

</div>
        </Link>
    </div>
    )
    
  });
  return  <>{renderList}</>

}


export default CharacterComponent
