import React from 'react'
import CharacterComponent from './CharacterComponent';
import { useEffect, useState,useCallback, useMemo } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { setCharacters ,fetchCharacters} from "../Redux/Actions/characterAction";



const CharacterListing = ()=> {
  const [page, setPageNum] = useState(1);
  const [characterId, setCharacterId] = useState(0);
   

  const characters = useSelector((state) => state);
  const dispatch=useDispatch();
 
        useEffect(() => {
        dispatch ( fetchCharacters(page));
        },[page]);

        console.log("Characters:" ,characters);

  return (
    <>
    <div className='container text-center ' style={{display:'flex'}}>
       <div className="row row-cols-4" style={{margin:'em'}} >
       <CharacterComponent characterId={characterId}/>
       </div>
       </div>
    <ul className="pagination justify-content-center">
      <li className="page-item"><a className="page-link"onClick={()=>{
        setPageNum((page)=>page-1);
        setCharacterId((characterId)=>characterId-50)
      }} >Previous</a></li>
      
      <li className="page-item"><a className="page-link" onClick={()=>{
        setPageNum((page)=>page+1);
        setCharacterId((characterId)=>characterId+50)
      }} >Next</a></li>
    </ul>
  
   </>
  
  )

  }
export default CharacterListing
