import React, { useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchCharacter,
  removeSelectedCharacter,
 
} from "../Redux/Actions/characterAction";


const CharacterDetails = () => {
  
  let character = useSelector((state) => state.character);
  const { characterId } = useParams();
  const { aliases,name, gender,born,culture} = character;
  const dispatch = useDispatch();
  console.log(characterId)
  console.log(character)

  const fetchCharacterDetail = async (id) => {
    const response = await axios
      .get(`https://www.anapioficeandfire.com/api/characters/${characterId}`)
      .catch((err) => {
        console.log("Err: ", err);
      });
    dispatch(selectedCharacter(response.data));
  };

  useEffect(() => {
    if (characterId && characterId !== "") fetchCharacterDetail(characterId);
    return () => {
      dispatch(removeSelectedCharacter());
    };
  }, [characterId]);

  return (
    <div className="ui grid center container" >
      {Object.keys(character).length === 0 ? (
        <h1 style={{margin:'30px'}}>...Loading</h1>
      ) : (
        <div className="ui placeholder segment">
          <div className="ui two column stackable center aligned grid">
            <div className="middle aligned row">
              <div className="column rp">
                <h1>{name}</h1>
                <h2>{aliases}</h2>
                <h2>
                  <a className="ui teal tag label">{culture}</a>
                </h2>
                <h3 className="ui brown block header">{born}</h3>
                <p>{gender}</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
export default CharacterDetails;
