import React from 'react';
import { useSelector } from "react-redux";
import {Link} from "react-router-dom"

const CharacterComponent = () => {
  const characters = useSelector((state) => state.allCharacters.characters);
  let characterId=0;
  // console.log("Characters:" ,characters);
  const renderList=characters.map((character)=>{
    characterId++;
    const { name, gender,culture} = character;
    
    return(
      <>
      <div className="three wide column" key={characterId}>
        {/* <Link to={`https://www.anapioficeandfire.com/api/characters/${id}`}> */}
        <Link to={`characters/${characterId}`}>
        <div className="ui link cards" style={{marginTop:'5px'}}>
          <div className="card">
                 {characterId}
            <div className="content">
               <div className="header"> {name}</div>
               <div className='meta gender'>{gender}</div>
               <div className='culture'>{culture}</div>
            </div>
          </div>
        </div>
        </Link>
    </div>
    <nav aria-label="Page navigation example">
    <ul class="pagination">
      <li class="page-item"><a class="page-link" href="#">Previous</a></li>
      <li class="page-item"><a class="page-link" href="#">1</a></li>
      <li class="page-item"><a class="page-link" href="#">2</a></li>
      <li class="page-item"><a class="page-link" href="#">3</a></li>
      <li class="page-item"><a class="page-link" href="#">Next</a></li>
    </ul>
  </nav>
  </>
    )
    
  });
  return  <>{renderList}</>

}


export default CharacterComponent
